import { useState } from "react";

function Greeting() {
  const [changed, setChanged] = useState(false);

  function changeTextHandler() {
    setChanged(true);
  }
  return (
    <>
      <div>
        <h2>Hello</h2>
        {!changed &&  <p>Texh not changed</p> }

        <p>Good to see you</p>
        <button onClick={changeTextHandler}> Change Text</button>
      </div>
    </>
  );
}

export default Greeting;
